package greed

import "testing"

type isMatchCase struct {
	s, p string
	d    bool
}

var cs = []isMatchCase{
	isMatchCase{
		s: "acdcb",
		p: "a*c?b",
		d: false,
	},
	isMatchCase{
		s: "mississippi",
		p: "m??*ss*?i*pi",
		d: false,
	},
	isMatchCase{
		s: "",
		p: "**",
		d: false,
	},
}

func TestIsMatch(t *testing.T) {
	for i, c := range cs {
		g := isMatch(c.s, c.p)
		if g != c.d {
			t.Fatalf("%d desire %v  get %v", i, c.d, g)
		}

	}
}
